import React, { useState} from 'react'
import { Button, Menu } from 'semantic-ui-react'
import Login from "../authentication/Login";
import appMenuReducer from './AppMenuReducer'

const initState = {
    username: '',
    pwd: '',
    isLoggedIn: false,
    loginOpen: false
}


export default function AppMenu (){
    const [reducer, dispatch] = React.useReducer(loginReducer, initState);
    const [open, setOpen] = React.useState(false)

    return (
        <div>
            <Menu size='large'>
                <Menu.Item
                name='Heytour'
                />
                <Menu.Menu position='right'>
                <Menu.Item>
                    {reducer.isLoggedIn?(<div>{reducer.username}</div>):
                    (<Button primary
                        onClick={()=> dispatch({type: 'notLoggedIn'})}
                    >
                        Login
                    </Button>)}
                </Menu.Item>
                </Menu.Menu>
            </Menu>
            {reducer.loginOpen &&
            <Login
                open={()=> setOpen(true)}
                onLogin={(username)=> dispatch({type: 'loggedIn', payload: username})}
                onClose={()=> dispatch({type: 'openModal'})}
            />
            }
        </div>  
    )

}
