import React, { useState} from 'react'
import { Button, Menu } from 'semantic-ui-react'
import Auth from "../authentication/Auth";
import appMenuReducer from './AppMenuReducer'

export default function AppMenu (){
    const [state, dispatch] = React.useReducer(appMenuReducer, {
        user: {},
        authenticated: false,
        authOpen: false
    });

    function handleOpenAuth() {
        dispatch ({type: 'AUTHENTICATING'});
    }

    function handleAuthClose () {
        dispatch ({type: 'UNAUTHENTICATED'});
    }

    function handloLogin (user) {
        dispatch ({type: 'AUTHENTICATED'})
    }

    function handloLogout (user) {
        dispatch ({type: 'UNAUTHENTICATED'})
    }
    return (
        <div>
            <Menu size='large'>
                <Menu.Item
                name='Heytour'
                />
                <Menu.Menu position='right'>
                <Menu.Item>
                    {state.isLoggedIn?(<div>{state.user}</div>):
                    (<Button primary
                        onClick={()=> dispatch({type: 'notLoggedIn'})}
                    >
                        Login
                    </Button>)}
                </Menu.Item>
                </Menu.Menu>
            </Menu>

            {state.authOpen &&
            <Auth
                open={state.authOpen}
                onLogin={handleAuthClose}
                onClose={handloLogin}
            />
            }
        </div>  
    )

}
