import React, { useState} from 'react'
import { Button, Menu } from 'semantic-ui-react'
// import Login from "../authentication/Login";
import {Login} from "../authentication/Login"

const initState = {
    username: '',
    pwd: '',
    isLoading: false,
    error: '',
    isLoggedIn: false,
}

function reducer(state, action) {
    switch (action.type) {
        case 'Login' :
            return {
                ...state,
                isLoading: true,
                error: '',
            }
        case 'success':
            return {
                ...state,
                isLoggedIn: true,
                isLoading: false,
            }
        case 'error':
            return {
                ...state,
                error: action.payload.error,
                name: '',
                pwd: '',
                isLoading: false,
            }
        default: 
            return state;
    }
}
const [state, dispatch] = useReducer(reducer, initialState);


