import React, { useState} from 'react'
import { Button, Menu } from 'semantic-ui-react'
import Login from "../authentication/Login";
// import {Login} from "../authentication/Login"

const initState = {
    username: '',
    pwd: '',
    isLoggedIn: false,
    loginOpen: false
}

function loginReducer(state, action) {
    switch (action.type) {
        case 'loggedIn' :
            return {
                ...state,
                isLoggedIn: true,
                loginOpen: false,
            }
        case 'notLoggedIn':
            return {
                ...state,
                isLoggedIn: false,
                loginOpen: true,
            }
        default: 
            return state;
    }
}

export default function AppMenu (){
    const [reducer, dispatch] = React.useReducer(loginReducer, initState);

    return (
        <div>
            <Menu size='large'>
                <Menu.Item
                name='Heytour'
                />
                <Menu.Menu position='right'>
                <Menu.Item>
                    {reducer.isLoggedIn?(<div>{initState.username}</div>):
                    (<Button primary
                        onClick={()=> dispatch({type: 'notLoggedIn'})}
                    >
                        Login
                    </Button>)}
                </Menu.Item>
                </Menu.Menu>
            </Menu>
            {initState.loginOpen &&
            <Login
                open={()=> dispatch({type: 'notLoggedIn'})}
                onClose={()=> dispatch({type: 'notLoggedIn'})}
                onLogin={()=> dispatch({type: 'loggedIn'})}
            />
            }
        </div>  
    )

}
