import React from 'react'
import { Container, Grid, Icon, Segment, Button, Modal  } from 'semantic-ui-react'
import { useJobList } from './JobListAPI'
import JobListContent from './JobListContent';
import JobForm from '../createJobs/JobForm';
import { useReducer } from 'react';
import { useState} from 'react';

let style = {
  grid : {margin:"30px"},
}

const JobFormState = {
  setOpen: false
}

function setFormReducer(state, action) {
  switch (action.type) {
      case 'setOpen' :
          return {
              ...state,
              OpenModal: true
          }
      case 'setClose':
          return {
              ...state,
              OpenModal: false
          }
      default: 
          return state;
  }
}

export default function JobList() {
  const [jobList, setJobListFilter] = useJobList(null);
  const [state, dispatch] = React.useReducer(setFormReducer, JobFormState);
  const [open, setOpen] = useState(false);


  return (
    <Container fluid textAlign='center'>
      <Button basic color='blue'>
          <Icon name='plus'/>
          <JobForm
            open={()=> setOpen(true)} 
            onClose={()=> dispatch({type: 'setClose'})}
          /> Create the new Job 
      </Button>
      <Grid style={style.grid}>
      {jobList.data && jobList.data.map((job) => (
          <JobListContent
            key={job.id}
            isLoading={jobList.isLoading}
            job={job}
          />
        ))}

      </Grid>
    </Container>   
  )

}
